function updateAgentSystem(world, timeStep) {
    const agents = world.getEntitiesWithComponent('AgentComponent');

    for (const agentId of agents) {
        const agent = world.getComponent(agentId, 'AgentComponent');

        switch (agent.current_action) {
            case 'idle':
                handleIdleAgent(world, agentId);
                break;
            case 'approaching_fire':
                handleApproachingAgent(world, agentId);
                break;
            case 'suppressing_fire':
                handleSuppressingAgent(world, agentId);
                break;
            case 'returning_to_refill':
                handleReturningToRefillAgent(world, agentId);
                break;
            case 'refilling':
                handleRefillingAgent(world, agentId);
                break;
        }
    }
}

function updateMovementSystem(world, timeStep) {
    const entitiesWithTargets = world.getEntitiesWithComponent('TargetComponent');

    for (const entityId of entitiesWithTargets) {
        const agent = world.getComponent(entityId, 'AgentComponent');

        // 如果是智能体且正在执行特定动作，则不移动
        if (agent && (agent.current_action === 'suppressing_fire' || agent.current_action === 'refilling')) {
            continue;
        }

        const position = world.getComponent(entityId, 'PositionComponent');
        const target = world.getComponent(entityId, 'TargetComponent');

        if (position && target) {
            moveTowardsTarget(position, target, agent?.speed || 2.0, timeStep);
        }
    }
}

function updateFireSystem(world, timeStep) {
    // 1. 自然冷却所有可燃实体
    applyCooling(world, timeStep);

    // 2. 检查点燃条件
    checkIgnition(world);

    // 3. 处理燃烧过程
    processBurning(world, timeStep);

    // 4. 热量传播
    propagateHeat(world, timeStep);

    // 5. 处理压力容器
    handlePressureVessels(world, timeStep);
}

function updateInteractionSystem(world, timeStep) {
    const agents = world.getEntitiesWithComponent('AgentComponent');

    for (const agentId of agents) {
        const target = world.getComponent(agentId, 'TargetComponent');
        const inventory = world.getComponent(agentId, 'InventoryComponent');
        const emitter = world.getComponent(agentId, 'EmitterComponent');

        if (target && inventory && emitter && emitter.is_active) {
            processInteraction(world, agentId, target.target_entity_id, timeStep);
        }
    }
}

function updateExplosionSystem(world, timeStep) {
    const pressureVessels = world.getEntitiesWithComponent('PressureVesselComponent');

    for (const vesselId of pressureVessels) {
        const vessel = world.getComponent(vesselId, 'PressureVesselComponent');

        if (vessel.internal_pressure >= vessel.max_pressure) {
            triggerExplosion(world, vesselId, vessel);
        }
    }
}

function updateResourceSystem(world, timeStep) {
    const agents = world.getEntitiesWithComponent('AgentComponent');

    for (const agentId of agents) {
        const agent = world.getComponent(agentId, 'AgentComponent');

        if (agent.current_action === 'suppressing_fire') {
            handleResourceConsumption(world, agentId, timeStep);
        } else if (agent.current_action === 'refilling') {
            handleResourceRefill(world, agentId, timeStep);
        }
    }
}
